              <Input
                type="text"
                placeholder={searchConfig?.placeholderText || "Zoek activiteiten..."}
                value={searchQuery}
                onChange={(e) => {
                  console.log('Destination page search input changed:', e.target.value);
                  setSearchQuery(e.target.value);
                }}
                onKeyDown={(e) => {
                  console.log('Destination page key pressed:', e.key);
                  if (e.key === 'Enter') {
