// Image Upload Field component
function ImageUploadField({ label, value, onChange, placeholder, fileName, destination, entityId, entityName }: {
  label: string;
  value: string;
  onChange: (value: string) => void;
  placeholder?: string;
  fileName?: string;
  destination?: string;
  entityId?: string | number;
  entityName?: string;
}) {
  const [isUploading, setIsUploading] = useState(false);
  const { toast } = useToast();

  const handleFileUpload = async (file: File) => {
    setIsUploading(true);
    try {
      const result = await uploadFile({
        file,
        fileName,
        destination: destination || 'destinations',
        type: 'image',
        entityId,
        entityName
      });
      onChange(result.imagePath || '');
      toast({ title: "Success", description: "Afbeelding succesvol geÃ¼pload" });
    } catch (error) {
      toast({ title: "Error", description: "Upload mislukt", variant: "destructive" });
    } finally {
      setIsUploading(false);
    }
  };

  return (
    <div>
      <Label htmlFor={`upload-${label}`}>{label}</Label>
      <div className="flex gap-2">
        <Input
          id={`upload-${label}`}
          type="text"
          placeholder={placeholder || "/images/destinations/example.jpg"}
          value={value}
          onChange={(e) => onChange(e.target.value)}
          className="flex-1"
        />
        <Button
          type="button"
          variant="outline"
          size="sm"
          disabled={isUploading}
          onClick={() => document.getElementById(`file-${label}`)?.click()}
        >
          {isUploading ? "Uploading..." : <Upload className="h-4 w-4" />}
        </Button>
        <input
          id={`file-${label}`}
          type="file"
          accept="image/*"
          className="hidden"
          onChange={(e) => {
            const file = e.target.files?.[0];
            if (file) handleFileUpload(file);
          }}
        />
      </div>
      {value && (
        <div className="mt-2">
          <img src={value} alt="Preview" className="h-32 w-48 object-cover rounded border" />
        </div>
      )}
    </div>
  );
}

